import React, { Component } from 'react';
import logo from './LogoMakr_7sWE81.png';
import './App.css';
import Header from './components/Header'
import Result from './components/Result'
import Intro from './components/Intro'
import scrollToComponent from 'react-scroll-to-component';

class App extends Component {

  state = {

    report: null,
    vitamins: null


  }


  getVitamins = () => {
    fetch("http://localhost:3000/api/v1/vitamins",  {mode: 'no-cors'})
    .then(res =>res.json())
    .then(res => this.setState({vitamins: [...res] }))
 }

 getReport = () => {

  fetch("http://localhost:3001/report")
    .then(res =>res.json())
    .then( this.setState({report: [...res] }))
    this.getVitamins()
    console.log(this.state.vitamins)

}




   render() {

     const {vitamins,report} = this.state

     return (
       <div className="App">
        {!report ?
        <Header   getReport={this.getReport}/>
        :
         <Result vitamins={vitamins} report={report} />


        }








       </div>
     );
   }
 }

export default App;
