import React from 'react'
import logo from '../logo.svg';
import { Parallax } from 'react-scroll-parallax'
import Switch from '@material-ui/core/Switch';
import Slide from '@material-ui/core/Slide';

class VitaminStack extends React.Component  {



 getTrait = name => {
    return this.props.report.find(r => r.trait === `${name}`)
}


getScore = name => {
   return this.props.report.find(r => r.trait === `${name}`).score
}

getVitaminInfo = name => {
    return this.props.vitamins.find(r => r.name === `${name}`)

}



getStack = () => {
    const stack = []
    for (const i of this.props.report)  {
        if (this.getScore(i.trait) < 2 )
       { 
        switch (i.trait) {
            case "Folate":
            stack.push("Folic Acid");
            break;
            case "Skin Pigmentation":
            stack.push("Vitamin D");
            break;
            case "Sleep duration":
            stack.push("Melatonin");
            break;
            default:stack.push(i.trait) }
        }
    }
    return stack
}

toggleView = () => {
    this.setState({ checked: !this.state.checked });
  }


render () {

const {getScore, getStack,getTrait,getVitaminInfo} = this
const checked = this.props
return (

    <Parallax
    offsetYMax={25}
    offsetYMin={-35}
    >
<div className="result">
    <h2>Your Vitamin Stack</h2>
     {getStack().map(vitamin => 

      <div>

          <Slide  direction="down" in={checked} mountOnEnter unmountOnExit>
          <div className="info-container"> 
          <p>{vitamin}</p>
          <p>{getVitaminInfo(vitamin).sources}</p>
          <p>{getVitaminInfo(vitamin).rda}</p>
          <p>{getVitaminInfo(vitamin).benefits}</p>
          <p>{getVitaminInfo(vitamin).toxicity}</p>
          </div>
          </Slide>
    </div>
      )}
</div>
</Parallax>
    )
}
}
 export default VitaminStack
