import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { WordCloud } from "word-cloud-react";


class Diet extends Component {



    generateFoodMap  = () => {
        let array = []
        this.props.report.map(vitamin => 
            vitamin.sources.map(source => {
                if (array.find(val, idx => val.word === source)) {
                x = array.findIndex(val, idx => val.word === source) 
                 array[x] = {word: source, value: array[x].value + 1}
                }

                else { 
                    array.push({word: source, value: 1})
                }
            })
        )
        return array 
    }

   render() {

     return (
       <div className="food">
         <WordCloud 
            width={"auto"} 
            maxFont={40} 
            minFont={10} 
            logFunc={(x)=> Math.log2(x) * 5} 
            data={this.generateFoodMap()} 
            clickEvent={(x)=>console.log(x.word)} 
            color={['#71803F', '#F8AC1D','#598EC0','#E2543E','#1A3051','#F46F73','#8A87BB','#56CFCD','#297373','#FF8552','#F2E863','#C2F8CB','#3A6EA5','#FF6700','#C0C0C0','#4E4381','#523CBD',]}/>



       </div>
     );
   }
 }

export default  Diet;
