import React from 'react'
import logo from '../logo.svg';
import { Parallax } from 'react-scroll-parallax'

class VitaminStack extends React.Component  {

 getTrait = name => {
    return report.find(r => r.trait === `${name}`)
}


getScore = name => {
   return report.find(r => r.trait === `${name}`).score
}

getVitaminInfo = name => {
    return vitamins.find(r => r.name === `${name}`)

}



getStack = () => {
    const stack = []
    for (const i of report)  {
        if (getScore(i.trait) < 2 )
       { 
        switch (i.trait) {
            case "Folate":
            stack.push("Folic Acid");
            break;
            case "Skin Pigmentation":
            stack.push("Vitamin D");
            break;
            case "Sleep duration":
            stack.push("Melatonin");
            break;
            default:stack.push(i.trait) }
        }
    }
    return stack
}

handleChange = () => {
    this.setState(state => ({ checked: !state.checked }));
  }


render () {
return (

    <Parallax
    offsetYMax={25}
    offsetYMin={-35}
    >
<div className="result">
    <h2>Your Vitamin Stack</h2>
     {getStack().map(vitamin => 

      <div className="info-container"> 
       <Switch checked={checked} onChange={this.handleChange} aria-label="Collapse" />
          <Slide direction="up" in={checked} mountOnEnter unmountOnExit>
          <p>{vitamin}</p>
          <p>{getVitaminInfo(vitamin).sources}</p>
          <p>{getVitaminInfo(vitamin).rda}</p>
          <p>{getVitaminInfo(vitamin).benefits}</p>
          <p>{getVitaminInfo(vitamin).toxicity}</p>
          </Slide>
      </div>
      )}


</div>
</Parallax>
    )
}
}
 export default VitaminStack
