import React from 'react'
import logo from '../logo.svg';


const YourGenome = ({report}) => {

const getTrait = name => {
    return report.find(r => r.trait === `${name}`)
}


const getScore = name => {
   return report.find(r => r.trait === `${name}`).score
}

const getResultInfo = vitamin => {
    switch (vitamin.score) {
        case 0:
         return 'Lower blood serum level'
        break;
        case 1:
        return  'Slightly lower serum level'
        break;
        case 2:
        return 'Intermediate'
        break;
        case 3:
         return 'Slightly higher serum level'
        break;
        case 4:
         return 'Higher blood serum level'
        break;
    }

}








return (

<div className="result">
    <h2>Your phenotype Results</h2>
     {report.map(vitamin => {

      <div className="info-container"> 
          <p>{vitamin.trait}</p>
          <p>Score: {vitamin.score} - {getResultInfo(vitamin)}</p>

          }}



      </div>
     })}


</div>
    )
}
 export default YourGenome
