import React from 'react'
import logo from '../logo.svg';


const Result = ({report}) => {

const getTrait = name => {
    return report.find(r => r.trait === `${name}`)
}


const getScore = name => {
   return report.find(r => r.trait === `${name}`).score
}



const getStack = () => {
    const stack = []
    for (const i of report)  {
        if (getScore(i.trait) < 2 )
       {

        stack.push(i.trait) }
    }
    console.log(stack)
    refactorStack(stack)
}

const refactorStack = (s) => {
    console.log(s)
    s.map(vitamin => {
        switch (vitamin) {
            case "Folate":
            vitamin = "Folic Acid";
            case "Skin Pigmentation":
            vitamin = "Vitamin D";
            case "Sleep duration":
            vitamin = "Melatonin";
            default: vitamin = vitamin
        }
        return vitamin

    })
    console.log(s)
}









return (

<div className="splash">
    <h2>Your Vitamin Stack</h2>
     {getStack()}
<img src={logo} className="App-logo" alt="logo" />


</div>
    )
}
 export default Result
