import React, { Component, Fragment } from 'react';
import logo from './LogoMakr_7sWE81.png';
import './App.css';
import Header from './components/Header'
import VitaminStack from './components/VitaminStack'
import Intro from './components/Intro'
import YourGenome from './components/YourGenome'
import scrollToComponent from 'react-scroll-to-component';

class App extends Component {

  state = {

    report: null,
    vitamins: null


  }

 componentDidMount () {
   this.getVitamins()

 }

  getVitamins = () => {
    fetch("http://localhost:3000/api/v1/vitamins")
    .then(res =>res.json())
    .then(res => this.setState({vitamins: [...res] }))
 }

 getReport = () => {

  fetch("http://localhost:3001/report")
    .then(res =>res.json())
    .then( res => this.setState({report: [...res] }))


}




   render() {

     const {vitamins,report} = this.state

     return (
       <div className="App">
         <Fragment >
          {!report ?
          <Header getReport={this.getReport}/>
          :

          <div>
             <Header report={report} getReport={this.getReport}/>
             <YourGenome report={report} />

          <VitaminStack vitamins={vitamins} report={report} />
          </div>
        }
        </Fragment>





        </div>

     );
   }
 }

export default App;
