import React, { Component } from 'react';
import logo from './LogoMakr_7sWE81.png';
import './App.css';
import Header from './components/Header'
import Result from './components/Result'
import Intro from './components/Intro'
import scrollToComponent from 'react-scroll-to-component';

class App extends Component {

  state = {

    report: null,
    vitamins: null


  }


  componentDidMount() {
    fetch("http://localhost:3002/vitamins")
    .then(res =>res.json())
    .then(res => this.setState({report: [...res] }))
 }

 getReport = () => {
  fetch("http://localhost:3000/report")
    .then(res =>res.json())
    .then(res => this.setState({report: [...res] }))
   scrollToComponent(this.Yellow, { offset: 0, align: 'middle', duration: 500, ease:'inCirc'})
}




   render() {

     const report = this.state.report

     return (
       <div className="App">
        {!report ?
        <Header  ref={(section) => { this.Blue = section; }} getReport={this.getReport}/>
        :
         <Result ref={(section) => { this.Yellow = section; }}report={report} />


        }








       </div>
     );
   }
 }

export default App;
