import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';



class Form extends Component {





   render() {

    const buttonStyle = {

      background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
      borderRadius: 3,
      border: 0,
      color: 'white',
      height: 48,
      padding: '0 30px',
      boxShadow: '0 3px 2px 2px rgba(255, 105, 135, .3)',


    }

    const formStyle = {


    }

    const {handleChange, handleSubmit} = this.props

    const content =  [{
      question: 'Are you vegan?',
      label: 'vegan'
    },
    {
      question: 'Are you of African descent?',
      label: 'african'
    },
    {
      question: 'Are you pregnant or trying?',
      label: 'pregnant'
    }]

     return (

       <div className="form">
      <p>A few questions to take your lifestyle into account..</p>
      <Slider>

      {content.map(question =>

    //   <div>
    // <FormControl component="fieldset">
    //   <FormLabel component="legend">{question.question}</FormLabel>
    //   <RadioGroup
    //     aria-label={question.label}
    //     name={question.label}

    //     // value={this.props.{question.label}}
    //     onChange={this.handleChange}
    //   >
    //     <FormControlLabel value="Yes" control={<Radio />} label="Yes" />
    //     <FormControlLabel value="No" control={<Radio />} label="No" />
    //   </RadioGroup>
    // </FormControl>



        <div>
          <form>
          <legend className="legend">{question.question}</legend>
          <ul>
          <li>
          <input type="radio" id="f-option" value="yes" onClicke={handleChange} name={question.question} checked={this.props[question.label] === true} />

          <label for="f-option">Yes</label>
          <div className="check" value="yes" name={question.question}></div>
          </li>

        <li>
        <input type="radio" id="s-option"  value="no" name={question.label} onClick={handleChange} checked={this.props[question.label] === false}/>
        <label for="s-option">No</label>
        <div className="check"><div className="inside"></div></div>
        </li>
      </ul>
      </form>
    </div>




    )}

     <div>
       <Button style={buttonStyle} variant="contained" onClick={handleSubmit}>
            Proceed
          </Button>
     </div>


    </Slider>


       </div>
     );
   }
 }

export default Form;
